import React from 'react';
import { players } from './playerlist.js';
import { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../index.css'

function createAnswerPool(playerObj, teamArray) {
    let answerPool = [];
    answerPool.push(playerObj.team);
    for (let i = 0; i < 5; i++) {
        answerPool.push(teamArray[Math.floor(Math.random() * teamArray.length)]);
    }

    let currIndex = answerPool.length;

    while (currIndex != 0) {
        let randomIndex = Math.floor(Math.random() * currIndex);
        currIndex--;

        [answerPool[currIndex], answerPool[currIndex]] = [
            answerPool[randomIndex], answerPool[currIndex]];
    }

    return answerPool;
}

export function NewQuiz(props) {
    let [index, setIndex] = useState(0);
    let [question, setQuestion] = useState(props.players[index]);

    const uniqueTeams = new Set();
    players.forEach(player => {
        const team = player['team'];
        uniqueTeams.add(team);
    })
    const uniqueTeamsArray = Array.from(uniqueTeams);

    const p1AnswerPool = createAnswerPool(props.players[0], uniqueTeamsArray);
    const p2AnswerPool = createAnswerPool(props.players[1], uniqueTeamsArray);
    const p3AnswerPool = createAnswerPool(props.players[2], uniqueTeamsArray);
    const p4AnswerPool = createAnswerPool(props.players[3], uniqueTeamsArray);
    const p5AnswerPool = createAnswerPool(props.players[4], uniqueTeamsArray);

    Object.assign(props.players[0], {answerPool: p1AnswerPool});


    return (
        <body className='quizpage-body'>
        <div className="quiz-container">
            <h1>NBA Player Quiz</h1>
            <hr />
            <h2>{index + 1}. What team does {question.name}play for?</h2>
            <div className="img-container">
