import React from 'react';
import { players } from './playerlist.js';
import { useState, useRef } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../index.css'

function createAnswerPool(playerObj, teamArray) {
    let answerPool = [];
    answerPool.push(playerObj.team);
    for (let i = 0; i < 5; i++) {
        answerPool.push(teamArray[Math.floor(Math.random() * teamArray.length)]);
    }

    let currIndex = answerPool.length;

    while (currIndex != 0) {
        let randomIndex = Math.floor(Math.random() * currIndex);
        currIndex--;

        [answerPool[currIndex], answerPool[currIndex]] = [
            answerPool[randomIndex], answerPool[currIndex]];
    }

    return answerPool;
}

export function NewQuiz(props) {
    let [index, setIndex] = useState(0);
    let [question, setQuestion] = useState(props.players[index]);
    // stole this state function from YouTube
    let [lock, setLock] = useState(false);
    let [score, setScore] = useState(0);

    const checkAnswer = (chosen, answer) => {
        if(lock === false) {
            if(question.team === answer){
                chosen.target.classList.add("correct-answer");
                setLock(true);
                setScore(score=>score+1)
            }
            else {
                chosen.target.classList.add("incorrect-answer");
                setLock(true);
            }
        }
    }

    const nextQuestion = () => {
        setIndex(++index);
        setQuestion(props.players[index]);
        const elem = document.getElementById("answer");
        elem.classList.remove("incorrect-answer");
        elem.classList.remove("correct-answer");
    }

    return (
        <body className='quizpage-body'>
            <div className="quiz-container">
                <h1>NBA Player Quiz</h1>
                <hr />
                <h2>{index + 1}. What team does {question.name} play for?</h2>
                <div className="img-container">
                    <img src={question.imgSrc} className="d-block h-100" alt={question.name} />
                </div>
                <ul>
                    <li id="answer" onClick={(chosen)=>{checkAnswer(chosen, question.answerPool[0])}}>{question.answerPool[0]}</li>
                    <li id="answer" onClick={(chosen)=>{checkAnswer(chosen, question.answerPool[1])}}>{question.answerPool[1]}</li>
                    <li id="answer" onClick={(chosen)=>{checkAnswer(chosen, question.answerPool[2])}}>{question.answerPool[2]}</li>
                    <li id="answer" onClick={(chosen)=>{checkAnswer(chosen, question.answerPool[3])}}>{question.answerPool[3]}</li>
                </ul>
                <button onClick={nextQuestion}>Next</button>
                <div className='question-tracker'>
                    {index+1} of {props.players.length} Questions
                </div>
            </div>
        </body>
    )
}
