import React from 'react';
import { players } from './playerlist.js';
import { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../index.css'

function createAnswerPool(playerObj, teamArray) {
    let answerPool = [];
    answerPool.push(playerObj.team);
    for (let i = 0; i < 5; i++) {
        answerPool.push(teamArray[Math.floor(Math.random() * teamArray.length)]);
    }

    let currIndex = answerPool.length;

    while (currIndex != 0) {
        let randomIndex = Math.floor(Math.random() * currIndex);
        currIndex--;

        [answerPool[currIndex], answerPool[currIndex]] = [
            answerPool[randomIndex], answerPool[currIndex]];
    }

    return answerPool;
}

export function NewQuiz(props) {
    let [index, setIndex] = useState(0);
    let [question, setQuestion] = useState(props.players[index]);

    const randomPlayers = props.players;
    const p1 = randomPlayers[0];
    const p2 = randomPlayers[1];
    const p3 = randomPlayers[2];
    const p4 = randomPlayers[3];
    const p5 = randomPlayers[4];

    const uniqueTeams = new Set();
    players.forEach(player => {
        const team = player['team'];
        uniqueTeams.add(team);
    })
    const uniqueTeamsArray = Array.from(uniqueTeams);

    const p1AnswerPool = createAnswerPool(p1, uniqueTeamsArray);
    const p2AnswerPool = createAnswerPool(p2, uniqueTeamsArray);
    const p3AnswerPool = createAnswerPool(p3, uniqueTeamsArray);
    const p4AnswerPool = createAnswerPool(p4, uniqueTeamsArray);
    const p5AnswerPool = createAnswerPool(p5, uniqueTeamsArray);

    return (
        <body className='quizpage-body'>
        <div className="quiz-container">
            <h1>NBA Player Quiz</h1>
            <hr />
            <h2>{index + 1}. What team does {p1.name} play for?</h2>
            <div className="img-container">
                <img src={p1.imgSrc} className="d-block h-100" alt={p1.name} />
            </div>
            <ul>
                <li>{p1AnswerPool[0]}</li>
                <li>{p1AnswerPool[1]}</li>
                <li>{p1AnswerPool[2]}</li>
                <li>{p1AnswerPool[3]}</li>
            </ul>
            <button>Next</button>
            <div className='question-tracker'>
                1 of 5 Questions
            </div>
        </div>
        </body>
    )
}
